package com.cg.proj.web;import java.util.List;import javax.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import com.cg.proj.DTO.MechanicDTO;import com.cg.proj.DTO.SuccessMessageDTO;import com.cg.proj.DTO.UserDTO;import com.cg.proj.entity.Mechanic;import com.cg.proj.entity.User;import com.cg.proj.exceptions.MechanicNotFoundException;import com.cg.proj.exceptions.UserNotFoundException;import com.cg.proj.exceptions.ValidateException;import com.cg.proj.util.VehicleConstants;import com.cg.proj.service.MechanicService;import com.cg.proj.service.UserService;@RestControllerpublic class MechanicController {	@Autowired	private MechanicService mechanicservice;	@PostMapping("addmechanic")	public SuccessMessageDTO addMechanic(@Valid @RequestBody MechanicDTO mechanicdto, BindingResult br) throws ValidateException {		if (br.hasErrors())			throw new ValidateException(br.getFieldErrors());		mechanicservice.addMechanic(mechanicdto);		return new SuccessMessageDTO(VehicleConstants.MECHANIC_ADDED);	}	@GetMapping("getmechanic/{mechanicId}")	public Mechanic getMechanic(@PathVariable("mechanicId") int mechanicId) throws MechanicNotFoundException {		return mechanicservice.getMechanic(mechanicId);	}	@GetMapping("getallmechanic")	public List<Mechanic> getAllMechanic() {		return mechanicservice.getallMechanic();	}}