package com.cg.proj.web;import java.util.List;import javax.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.cg.proj.DTO.SuccessMessageDTO;import com.cg.proj.DTO.UserDTO;import com.cg.proj.entity.User;import com.cg.proj.exceptions.UserNotFoundException;import com.cg.proj.exceptions.ValidateException;import com.cg.proj.util.VehicleConstants;import com.cg.proj.service.UserService;@RestController@RequestMapping("/user")public class UserController {	@Autowired	private UserService userservice;	@PostMapping("/adduser")	public SuccessMessageDTO addUser(@Valid @RequestBody UserDTO userdto, BindingResult br) throws ValidateException {		if (br.hasErrors())			throw new ValidateException(br.getFieldErrors());		userservice.addUser(userdto);		return new SuccessMessageDTO(VehicleConstants.USER_ADDED);	}	@GetMapping("/getuser/{userId}")	public User getUser(@PathVariable("userId") int userId) throws UserNotFoundException {		return userservice.getUser(userId);	}	@GetMapping("/getalluser")	public List<User> getAllUser() {		return userservice.getallUser();	}}